import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';

const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  overflow: hidden;
`;

const floatAnimation = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  33% { transform: translateY(-20px) rotate(120deg); }
  66% { transform: translateY(20px) rotate(240deg); }
`;

const pulseAnimation = keyframes`
  0%, 100% { opacity: 0.3; transform: scale(1); }
  50% { opacity: 0.6; transform: scale(1.1); }
`;

const waveAnimation = keyframes`
  0% { transform: translateX(-100%) skewX(12deg); }
  100% { transform: translateX(100vw) skewX(12deg); }
`;

const GradientBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: radial-gradient(ellipse at top, #000814 0%, #001122 25%, #001d3d 50%, #003566 75%, #0353a4 100%);
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      135deg,
      rgba(255, 255, 255, 0.02) 0%,
      rgba(3, 83, 164, 0.05) 25%,
      rgba(0, 29, 61, 0.03) 50%,
      rgba(255, 255, 255, 0.01) 75%,
      rgba(0, 8, 20, 0.8) 100%
    );
  }
  
  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: 
      radial-gradient(circle at 20% 20%, rgba(255, 255, 255, 0.1) 0%, transparent 50%),
      radial-gradient(circle at 80% 80%, rgba(3, 83, 164, 0.15) 0%, transparent 50%),
      radial-gradient(circle at 40% 60%, rgba(0, 29, 61, 0.1) 0%, transparent 50%);
  }
`;

const FloatingOrb = styled(motion.div)`
  position: absolute;
  border-radius: 50%;
  filter: blur(60px);
  animation: ${floatAnimation} 20s ease-in-out infinite;
`;

const PulsingOrb = styled(motion.div)`
  position: absolute;
  border-radius: 50%;
  filter: blur(80px);
  animation: ${pulseAnimation} 8s ease-in-out infinite;
`;

const WaveElement = styled.div`
  position: absolute;
  width: 200%;
  height: 100px;
  background: linear-gradient(90deg, 
    transparent 0%, 
    rgba(255, 255, 255, 0.03) 25%, 
    rgba(3, 83, 164, 0.08) 50%, 
    rgba(255, 255, 255, 0.03) 75%, 
    transparent 100%
  );
  animation: ${waveAnimation} 15s linear infinite;
  transform: skewX(12deg);
`;

const Wave1 = styled(WaveElement)`
  top: 20%;
  animation-delay: 0s;
`;

const Wave2 = styled(WaveElement)`
  top: 60%;
  animation-delay: -5s;
  opacity: 0.6;
`;

const Wave3 = styled(WaveElement)`
  top: 80%;
  animation-delay: -10s;
  opacity: 0.4;
`;

const Orb1 = styled(FloatingOrb)`
  width: 400px;
  height: 400px;
  background: radial-gradient(circle, rgba(255, 255, 255, 0.1) 0%, rgba(3, 83, 164, 0.2) 50%, transparent 100%);
  top: -10%;
  left: -15%;
  animation-delay: 0s;
`;

const Orb2 = styled(PulsingOrb)`
  width: 300px;
  height: 300px;
  background: radial-gradient(circle, rgba(3, 83, 164, 0.15) 0%, rgba(0, 29, 61, 0.1) 50%, transparent 100%);
  top: 50%;
  right: -5%;
`;

const Orb3 = styled(FloatingOrb)`
  width: 150px;
  height: 150px;
  background: linear-gradient(135deg, #10b981, #3b82f6);
  bottom: 20%;
  left: 20%;
`;

const GridPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    linear-gradient(rgba(255, 255, 255, 0.03) 1px, transparent 1px),
    linear-gradient(90deg, rgba(255, 255, 255, 0.03) 1px, transparent 1px);
  background-size: 50px 50px;
  mask-image: radial-gradient(circle at 50% 50%, black 40%, transparent 80%);
`;

const NoiseOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='1' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='0.02'/%3E%3C/svg%3E");
`;

function Background() {
  return (
    <BackgroundContainer>
      <GradientBackground />
      <GridPattern />
      <NoiseOverlay />
      
      <Orb1
        animate={{
          x: [0, 50, 0],
          y: [0, 30, 0],
        }}
        transition={{
          duration: 20,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      <Orb2
        animate={{
          x: [0, -30, 0],
          y: [0, 50, 0],
        }}
        transition={{
          duration: 15,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      <Orb3
        animate={{
          x: [0, 40, 0],
          y: [0, -20, 0],
        }}
        transition={{
          duration: 25,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
    </BackgroundContainer>
  );
}

export default Background;
