import React, { useState, useEffect } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import TicketForm from './components/TicketForm';
import TicketResponse from './components/TicketResponse';
import Header from './components/Header';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    background: #0a0a0a;
    color: #ffffff;
    min-height: 100vh;
    overflow-x: hidden;
    position: relative;
    cursor: default; /* Restore default cursor for better usability */
  }

  html {
    scroll-behavior: smooth;
  }

  /* Restore cursor for interactive elements */
  button, input, textarea, select, a, [role="button"] {
    cursor: pointer !important;
  }

  input, textarea {
    cursor: text !important;
  }

  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: #1a1a1a;
  }

  ::-webkit-scrollbar-thumb {
    background: linear-gradient(45deg, #6366f1, #8b5cf6);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(45deg, #4f46e5, #7c3aed);
  }
`;

const CustomCursor = styled.div`
  position: fixed;
  width: 12px;
  height: 12px;
  background: rgba(139, 92, 246, 0.6);
  border-radius: 50%;
  pointer-events: none;
  z-index: 9999;
  transform: translate(-50%, -50%);
  transition: all 0.1s ease-out;
  mix-blend-mode: difference;
  
  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 24px;
    height: 24px;
    background: rgba(59, 130, 246, 0.2);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    animation: simplePulse 3s ease-in-out infinite;
  }
  
  @keyframes simplePulse {
    0%, 100% {
      transform: translate(-50%, -50%) scale(0.5);
      opacity: 0.5;
    }
    50% {
      transform: translate(-50%, -50%) scale(1);
      opacity: 0.8;
    }
  }
`;

const AnimatedBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  background: linear-gradient(135deg, #0a0a0a 0%, #1a0a2e 40%, #16213e 100%);
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: -20%;
    left: -20%;
    width: 140%;
    height: 140%;
    background: radial-gradient(
      circle at var(--mouse-x, 50%) var(--mouse-y, 50%),
      rgba(139, 92, 246, 0.15) 0%,
      rgba(236, 72, 153, 0.12) 30%,
      rgba(59, 130, 246, 0.08) 60%,
      transparent 80%
    );
    transition: all 0.3s ease-out;
    will-change: background;
  }
  
  &::after {
    content: '';
    position: absolute;
    top: -10%;
    left: -10%;
    width: 120%;
    height: 120%;
    background: radial-gradient(
      ellipse at calc(var(--mouse-x, 50%) + 20%) calc(var(--mouse-y, 50%) - 15%),
      rgba(16, 185, 129, 0.1) 0%,
      rgba(99, 102, 241, 0.08) 40%,
      transparent 70%
    );
    animation: gentleFloat 15s ease-in-out infinite;
    transition: opacity 0.3s ease-out;
  }

  &:hover::before {
    background: radial-gradient(
      circle at var(--mouse-x, 50%) var(--mouse-y, 50%),
      rgba(139, 92, 246, 0.25) 0%,
      rgba(236, 72, 153, 0.18) 25%,
      rgba(59, 130, 246, 0.15) 50%,
      rgba(16, 185, 129, 0.1) 70%,
      transparent 85%
    );
  }

  &:hover::after {
    opacity: 0.8;
    animation-duration: 8s;
  }

  @keyframes gentleFloat {
    0%, 100% {
      transform: translate(0, 0) rotate(0deg);
      opacity: 0.6;
    }
    25% {
      transform: translate(10px, -5px) rotate(1deg);
      opacity: 0.8;
    }
    50% {
      transform: translate(-5px, 10px) rotate(-0.5deg);
      opacity: 0.7;
    }
    75% {
      transform: translate(8px, -8px) rotate(0.8deg);
      opacity: 0.9;
    }
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
  display: flex;
  flex-direction: column;
`;

const MainContent = styled.main`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  position: relative;
  z-index: 2;
`;

const ContentWrapper = styled(motion.div)`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

function App() {
  const [ticketResponse, setTicketResponse] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      const x = (e.clientX / window.innerWidth) * 100;
      const y = (e.clientY / window.innerHeight) * 100;
      
      setCursorPosition({ x: e.clientX, y: e.clientY });
      
      // Update CSS custom properties for fluid background movement (throttled)
      document.documentElement.style.setProperty('--mouse-x', `${x}%`);
      document.documentElement.style.setProperty('--mouse-y', `${y}%`);
    };

    // Use throttle instead of debounce for smoother real-time tracking
    const throttledMouseMove = throttle(handleMouseMove, 50); // 20fps instead of 100fps
    window.addEventListener('mousemove', throttledMouseMove);
    
    return () => {
      window.removeEventListener('mousemove', throttledMouseMove);
    };
  }, []);

  // Throttle function for better performance than debounce for mouse movement
  function throttle(func, limit) {
    let inThrottle;
    return function(...args) {
      if (!inThrottle) {
        func.apply(this, args);
        inThrottle = true;
        setTimeout(() => inThrottle = false, limit);
      }
    };
  }

  const handleTicketSubmit = (response) => {
    setTicketResponse(response);
  };

  const handleNewTicket = () => {
    setTicketResponse(null);
  };

  const handleLoadingChange = (loading) => {
    setIsLoading(loading);
  };

  return (
    <>
      <GlobalStyle />
      <AnimatedBackground />
      <CustomCursor 
        style={{
          left: `${cursorPosition.x}px`,
          top: `${cursorPosition.y}px`
        }}
      />
      <AppContainer>
        <Header />
        <MainContent>
          <ContentWrapper
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <AnimatePresence mode="wait">
              {!ticketResponse ? (
                <motion.div
                  key="ticket-form"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <TicketForm 
                    onSubmit={handleTicketSubmit}
                    onLoadingChange={handleLoadingChange}
                    isLoading={isLoading}
                  />
                </motion.div>
              ) : (
                <motion.div
                  key="ticket-response"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <TicketResponse 
                    response={ticketResponse}
                    onNewTicket={handleNewTicket}
                  />
                </motion.div>
              )}
            </AnimatePresence>
          </ContentWrapper>
        </MainContent>
      </AppContainer>
    </>
  );
}

export default App;
