import { createGlobalStyle } from 'styled-components';

export const GlobalStyles = createGlobalStyle`
  body[data-theme='dark'] {
    background: #181a20;
    color: #e5e7eb;
    transition: background 0.4s cubic-bezier(0.4,0,0.2,1), color 0.4s cubic-bezier(0.4,0,0.2,1);
  }
  body[data-theme='light'] {
    background: #f3f4f6;
    color: #23272f;
    transition: background 0.4s cubic-bezier(0.4,0,0.2,1), color 0.4s cubic-bezier(0.4,0,0.2,1);
  }
  
  [data-theme='light'] .formatted-step .step-number {
    background: linear-gradient(135deg, #6366f1, #8b5cf6) !important;
    color: #fff !important;
  }
  [data-theme='light'] .formatted-step .step-text {
    color: #23272f !important;
  }
  [data-theme='light'] .formatted-bullet .bullet-marker {
    color: #6366f1 !important;
  }
  [data-theme='light'] .formatted-bullet .bullet-text {
    color: #23272f !important;
  }
  [data-theme='light'] h1, [data-theme='light'] h2, [data-theme='light'] h3, [data-theme='light'] h4 {
    color: #23272f !important;
  }
  [data-theme='light'] code {
    background: #f3f4f6 !important;
    color: #8b5cf6 !important;
  }
`;
