import React from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';

const float = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(180deg); }
`;

const pulse = keyframes`
  0%, 100% { opacity: 0.5; transform: scale(1); }
  50% { opacity: 0.8; transform: scale(1.05); }
`;

const drift = keyframes`
  0% { transform: translateX(-100px) translateY(0px); }
  50% { transform: translateX(100px) translateY(-30px); }
  100% { transform: translateX(-100px) translateY(0px); }
`;

const spiral = keyframes`
  0% { transform: rotate(0deg) translateX(30px) rotate(0deg); }
  100% { transform: rotate(360deg) translateX(30px) rotate(-360deg); }
`;

const wave = keyframes`
  0%, 100% { transform: translateY(0px) translateX(0px) rotate(0deg); }
  25% { transform: translateY(-15px) translateX(10px) rotate(90deg); }
  50% { transform: translateY(0px) translateX(20px) rotate(180deg); }
  75% { transform: translateY(15px) translateX(10px) rotate(270deg); }
`;

const bounce = keyframes`
  0%, 100% { transform: translateY(0px) scale(1); }
  50% { transform: translateY(-25px) scale(1.1); }
`;

const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  overflow: hidden;
`;

const GradientBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: 
    radial-gradient(ellipse at top left, rgba(16, 185, 129, 0.15) 0%, transparent 50%),
    radial-gradient(ellipse at top right, rgba(99, 102, 241, 0.15) 0%, transparent 50%),
    radial-gradient(ellipse at bottom left, rgba(139, 92, 246, 0.15) 0%, transparent 50%),
    radial-gradient(ellipse at bottom right, rgba(236, 72, 153, 0.15) 0%, transparent 50%),
    linear-gradient(135deg, #0a0a0a 0%, #1a1a2e 25%, #16213e 50%, #1a1a2e 75%, #0a0a0a 100%);
`;

const FloatingGeometry = styled(motion.div)`
  position: absolute;
  opacity: 0.15;
  pointer-events: none;
`;

const HexagonShape = styled(FloatingGeometry)`
  width: 60px;
  height: 60px;
  background: linear-gradient(135deg, #10b981, #3b82f6);
  clip-path: polygon(25% 0%, 75% 0%, 100% 50%, 75% 100%, 25% 100%, 0% 50%);
  animation: ${float} 8s ease-in-out infinite;
`;

const CircleShape = styled(FloatingGeometry)`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: linear-gradient(135deg, #8b5cf6, #ec4899);
  animation: ${bounce} 6s ease-in-out infinite;
`;

const TriangleShape = styled(FloatingGeometry)`
  width: 50px;
  height: 50px;
  background: linear-gradient(135deg, #f59e0b, #ef4444);
  clip-path: polygon(50% 0%, 0% 100%, 100% 100%);
  animation: ${wave} 10s ease-in-out infinite;
`;

const DiamondShape = styled(FloatingGeometry)`
  width: 45px;
  height: 45px;
  background: linear-gradient(135deg, #06b6d4, #3b82f6);
  clip-path: polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%);
  animation: ${spiral} 12s linear infinite;
`;

const StarShape = styled(FloatingGeometry)`
  width: 35px;
  height: 35px;
  background: linear-gradient(135deg, #fbbf24, #f59e0b);
  clip-path: polygon(50% 0%, 61% 35%, 98% 35%, 68% 57%, 79% 91%, 50% 70%, 21% 91%, 32% 57%, 2% 35%, 39% 35%);
  animation: ${pulse} 7s ease-in-out infinite;
`;

const OctagonShape = styled(FloatingGeometry)`
  width: 55px;
  height: 55px;
  background: linear-gradient(135deg, #84cc16, #10b981);
  clip-path: polygon(30% 0%, 70% 0%, 100% 30%, 100% 70%, 70% 100%, 30% 100%, 0% 70%, 0% 30%);
  animation: ${float} 14s ease-in-out infinite reverse;
`;

const PentagonShape = styled(FloatingGeometry)`
  width: 48px;
  height: 48px;
  background: linear-gradient(135deg, #ef4444, #dc2626);
  clip-path: polygon(50% 0%, 100% 38%, 82% 100%, 18% 100%, 0% 38%);
  animation: ${wave} 9s ease-in-out infinite reverse;
`;

const RingShape = styled(FloatingGeometry)`
  width: 42px;
  height: 42px;
  border: 3px solid;
  border-image: linear-gradient(135deg, #8b5cf6, #06b6d4) 1;
  border-radius: 50%;
  animation: ${spiral} 8s linear infinite reverse;
`;

const CrossShape = styled(FloatingGeometry)`
  width: 38px;
  height: 38px;
  background: linear-gradient(135deg, #f97316, #ea580c);
  clip-path: polygon(40% 0%, 60% 0%, 60% 40%, 100% 40%, 100% 60%, 60% 60%, 60% 100%, 40% 100%, 40% 60%, 0% 60%, 0% 40%, 40% 40%);
  animation: ${bounce} 11s ease-in-out infinite;
`;

const ParticleOrb = styled(motion.div)`
  position: absolute;
  border-radius: 50%;
  background: radial-gradient(circle, rgba(99, 102, 241, 0.6) 0%, rgba(99, 102, 241, 0.1) 70%, transparent 100%);
  filter: blur(1px);
  animation: ${drift} 20s ease-in-out infinite;
`;

const SmallOrb = styled(ParticleOrb)`
  width: 4px;
  height: 4px;
`;

const MediumOrb = styled(ParticleOrb)`
  width: 8px;
  height: 8px;
`;

const GlowingDots = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    radial-gradient(circle at 20% 20%, rgba(16, 185, 129, 0.3) 1px, transparent 1px),
    radial-gradient(circle at 80% 40%, rgba(99, 102, 241, 0.3) 1px, transparent 1px),
    radial-gradient(circle at 40% 80%, rgba(139, 92, 246, 0.3) 1px, transparent 1px),
    radial-gradient(circle at 70% 10%, rgba(236, 72, 153, 0.3) 1px, transparent 1px);
  background-size: 200px 200px, 300px 300px, 250px 250px, 180px 180px;
  animation: ${drift} 30s ease-in-out infinite;
`;

const WavePattern = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  width: 200%;
  height: 100px;
  background: linear-gradient(90deg, transparent 0%, rgba(16, 185, 129, 0.1) 50%, transparent 100%);
  transform: skewY(-2deg);
  animation: ${drift} 25s ease-in-out infinite;
`;

const ModernGrid = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    linear-gradient(rgba(255, 255, 255, 0.02) 1px, transparent 1px),
    linear-gradient(90deg, rgba(255, 255, 255, 0.02) 1px, transparent 1px);
  background-size: 100px 100px;
  mask-image: radial-gradient(ellipse at center, black 30%, transparent 80%);
  opacity: 0.5;
`;

function Background() {
  return (
    <BackgroundContainer>
      <GradientBackground />
      <ModernGrid />
      <GlowingDots />
      <WavePattern />
      
      {/* Floating Geometric Shapes */}
      <HexagonShape
        style={{ top: '15%', left: '10%' }}
        animate={{
          y: [0, -15, 0],
          rotate: [0, 360],
        }}
        transition={{
          duration: 12,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      <CircleShape
        style={{ top: '60%', right: '15%' }}
        animate={{
          y: [0, 20, 0],
          scale: [1, 1.2, 1],
        }}
        transition={{
          duration: 8,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      <TriangleShape
        style={{ bottom: '25%', left: '20%' }}
        animate={{
          y: [0, -25, 0],
          rotate: [0, -360],
        }}
        transition={{
          duration: 15,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      <HexagonShape
        style={{ top: '40%', right: '25%', transform: 'scale(0.7)' }}
        animate={{
          y: [0, 10, 0],
          rotate: [0, 180],
        }}
        transition={{
          duration: 10,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      {/* Floating Particle Orbs */}
      {[...Array(12)].map((_, i) => (
        <SmallOrb
          key={`small-${i}`}
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 20}s`,
          }}
        />
      ))}
      
      {[...Array(6)].map((_, i) => (
        <MediumOrb
          key={`medium-${i}`}
          style={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 15}s`,
          }}
        />
      ))}
    </BackgroundContainer>
  );
}

export default Background;
