import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { FaHeadset, FaRobot, FaLightbulb, FaSun, FaMoon } from 'react-icons/fa';
import { useTheme } from '../contexts/ThemeContext';

const HeaderContainer = styled(motion.header)`
  background: ${props => props.theme.headerBackground};
  backdrop-filter: blur(20px);
  border-bottom: 1px solid ${props => props.theme.headerBorder};
  padding: 1.5rem 2rem;
  position: sticky;
  top: 0;
  z-index: 100;
  box-shadow: 0 8px 32px ${props => props.theme.shadow};
  transition: all 0.3s ease;
`;

const HeaderContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-wrap: wrap;
  gap: 1rem;
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: 0.75rem;
`;

const LogoIcon = styled(motion.div)`
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, #6366f1, #8b5cf6);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 4px 20px rgba(99, 102, 241, 0.3);
  
  svg {
    color: white;
    font-size: 1.2rem;
  }
`;

const LogoText = styled.div`
  h1 {
    font-size: 1.5rem;
    font-weight: 700;
    background: linear-gradient(135deg, #ffffff, #e5e7eb);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin: 0;
  }
  
  p {
    font-size: 0.875rem;
    color: #9ca3af;
    margin: 0;
    font-weight: 400;
  }
`;

const StatusIndicators = styled.div`
  display: flex;
  align-items: center;
  gap: 1.5rem;
  
  @media (max-width: 768px) {
    flex-wrap: wrap;
    justify-content: center;
  }
`;

const StatusItem = styled(motion.div)`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 50px;
  font-size: 0.875rem;
  font-weight: 500;
`;

const StatusDot = styled.div`
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: ${props => props.status === 'online' ? '#10b981' : '#ef4444'};
  box-shadow: 0 0 8px ${props => props.status === 'online' ? '#10b981' : '#ef4444'};
`;

function Header() {
  return (
    <HeaderContainer
      initial={{ y: -100, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
    >
      <HeaderContent>
        <Logo>
          <LogoIcon
            whileHover={{ scale: 1.1, rotate: 5 }}
            whileTap={{ scale: 0.95 }}
          >
            <FaHeadset />
          </LogoIcon>
          <LogoText>
            <h1>TriageAI</h1>
            <p>AI-Powered Support</p>
          </LogoText>
        </Logo>
        
        <StatusIndicators>
          <StatusItem
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.2 }}
          >
            <FaRobot />
            <StatusDot status="online" />
            AI Online
          </StatusItem>
          
          <StatusItem
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.3 }}
          >
            <FaLightbulb />
            <StatusDot status="online" />
            Smart Search
          </StatusItem>
        </StatusIndicators>
      </HeaderContent>
    </HeaderContainer>
  );
}

export default Header;
