import React, { createContext, useContext, useState, useEffect } from 'react';

const ThemeContext = createContext();

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export const lightTheme = {
  background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
  cardBackground: 'rgba(255, 255, 255, 0.9)',
  cardBorder: 'rgba(0, 0, 0, 0.1)',
  text: '#1f2937',
  textSecondary: '#6b7280',
  headerBackground: 'rgba(255, 255, 255, 0.95)',
  headerBorder: 'rgba(0, 0, 0, 0.1)',
  inputBackground: 'rgba(255, 255, 255, 0.8)',
  inputBorder: 'rgba(0, 0, 0, 0.2)',
  shadow: 'rgba(0, 0, 0, 0.1)',
  glassmorphism: {
    background: 'rgba(255, 255, 255, 0.25)',
    border: 'rgba(255, 255, 255, 0.18)',
    backdrop: 'blur(20px)'
  }
};

export const darkTheme = {
  background: 'linear-gradient(135deg, #0f0f23 0%, #1a1a2e 50%, #16213e 100%)',
  cardBackground: 'rgba(255, 255, 255, 0.02)',
  cardBorder: 'rgba(255, 255, 255, 0.1)',
  text: '#ffffff',
  textSecondary: '#9ca3af',
  headerBackground: 'rgba(15, 15, 15, 0.95)',
  headerBorder: 'rgba(255, 255, 255, 0.1)',
  inputBackground: 'rgba(255, 255, 255, 0.05)',
  inputBorder: 'rgba(255, 255, 255, 0.2)',
  shadow: 'rgba(0, 0, 0, 0.3)',
  glassmorphism: {
    background: 'rgba(255, 255, 255, 0.02)',
    border: 'rgba(255, 255, 255, 0.1)',
    backdrop: 'blur(20px)'
  }
};

export const ThemeProvider = ({ children }) => {
  const [isDark, setIsDark] = useState(() => {
    const saved = localStorage.getItem('triageai-theme');
    return saved ? JSON.parse(saved) : true; // Default to dark theme
  });

  const currentTheme = isDark ? darkTheme : lightTheme;

  const toggleTheme = () => {
    setIsDark(prev => {
      const newTheme = !prev;
      localStorage.setItem('triageai-theme', JSON.stringify(newTheme));
      return newTheme;
    });
  };

  useEffect(() => {
    // Add transition to body for smooth theme switching
    document.body.style.transition = 'background 0.3s ease, color 0.3s ease';
    document.body.style.background = currentTheme.background;
    document.body.style.color = currentTheme.text;
    
    return () => {
      document.body.style.transition = '';
    };
  }, [currentTheme]);

  return (
    <ThemeContext.Provider value={{ 
      theme: currentTheme, 
      isDark, 
      toggleTheme 
    }}>
      {children}
    </ThemeContext.Provider>
  );
};
