import React, { useState, useEffect } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { motion } from 'framer-motion';

// Keyframe animations for the stickman walking
const walkCycle = keyframes`
  0%, 100% {
    transform: translateX(0) scaleX(1);
  }
  25% {
    transform: translateX(5px) scaleX(1.02);
  }
  50% {
    transform: translateX(10px) scaleX(1);
  }
  75% {
    transform: translateX(5px) scaleX(0.98);
  }
`;

const walkInFromLeft = keyframes`
  0% {
    transform: translateX(-200px);
    opacity: 0;
  }
  20% {
    opacity: 1;
  }
  100% {
    transform: translateX(0);
    opacity: 1;
  }
`;

// Tool animation - hammer swinging
const hammerSwing = keyframes`
  0%, 100% {
    transform: rotate(-10deg);
  }
  50% {
    transform: rotate(45deg);
  }
`;

// Wrench rotation
const wrenchTurn = keyframes`
  0% {
    transform: rotate(0deg);
  }
  25% {
    transform: rotate(15deg);
  }
  50% {
    transform: rotate(-15deg);
  }
  75% {
    transform: rotate(10deg);
  }
  100% {
    transform: rotate(0deg);
  }
`;

// Gear rotation for the broken item
const gearRotate = keyframes`
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
`;

// Sparks effect
const sparkle = keyframes`
  0%, 100% {
    opacity: 0;
    transform: scale(0) rotate(0deg);
  }
  50% {
    opacity: 1;
    transform: scale(1) rotate(180deg);
  }
`;

// Screen flicker effect for fixing
const screenFlicker = keyframes`
  0%, 100% {
    background: linear-gradient(135deg, #ef4444, #dc2626);
    box-shadow: 0 0 20px rgba(239, 68, 68, 0.5);
  }
  25% {
    background: linear-gradient(135deg, #f59e0b, #d97706);
    box-shadow: 0 0 20px rgba(245, 158, 11, 0.5);
  }
  75% {
    background: linear-gradient(135deg, #10b981, #059669);
    box-shadow: 0 0 20px rgba(16, 185, 129, 0.5);
  }
`;

const LogoContainer = styled.div`
  position: relative;
  width: 100px;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  cursor: pointer;
  
  &:hover {
    .stickman {
      animation: ${walkCycle} 0.8s infinite ease-in-out;
    }
    
    .tools {
      opacity: 1;
    }
    
    .sparks {
      opacity: 1;
    }
  }
`;

const BrokenItem = styled.div`
  position: absolute;
  right: 10px;
  top: 50%;
  transform: translateY(-50%);
  width: 30px;
  height: 30px;
  border-radius: 8px;
  transition: all 0.5s ease;
  
  ${props => props.isFixed ? css`
    background: linear-gradient(135deg, #10b981, #059669);
    box-shadow: 0 0 20px rgba(16, 185, 129, 0.5);
    
    &::before {
      content: '✓';
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      color: white;
      font-weight: bold;
      font-size: 16px;
    }
  ` : css`
    animation: ${screenFlicker} 2s ease-in-out infinite;
    
    &::before {
      content: '⚠';
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      color: white;
      font-weight: bold;
      font-size: 16px;
    }
  `}
  
  &::after {
    content: '';
    position: absolute;
    top: -5px;
    left: -5px;
    right: -5px;
    bottom: -5px;
    border: 2px solid;
    border-color: ${props => props.isFixed ? '#10b981' : '#ef4444'};
    border-radius: 10px;
    animation: ${gearRotate} 3s linear infinite;
    opacity: 0.7;
  }
`;

const Stickman = styled.div`
  position: absolute;
  left: 20px;
  top: 50%;
  transform: translateY(-50%);
  width: 20px;
  height: 35px;
  transition: all 0.3s ease;
  
  ${props => props.isWalkingIn && css`
    animation: ${walkInFromLeft} 2s ease-out forwards;
  `}
  
  // Head
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 8px;
    height: 8px;
    background: #0ea5e9;
    border-radius: 50%;
    box-shadow: 0 0 10px rgba(14, 165, 233, 0.5);
  }
  
  // Body
  &::after {
    content: '';
    position: absolute;
    top: 8px;
    left: 50%;
    transform: translateX(-50%);
    width: 2px;
    height: 15px;
    background: #0ea5e9;
    box-shadow: 0 0 5px rgba(14, 165, 233, 0.3);
  }
`;

const StickmanArms = styled.div`
  position: absolute;
  top: 12px;
  left: 50%;
  transform: translateX(-50%);
  width: 12px;
  height: 2px;
  background: #0ea5e9;
  box-shadow: 0 0 5px rgba(14, 165, 233, 0.3);
  
  &::before,
  &::after {
    content: '';
    position: absolute;
    width: 6px;
    height: 2px;
    background: #0ea5e9;
    box-shadow: 0 0 5px rgba(14, 165, 233, 0.3);
  }
  
  &::before {
    top: -2px;
    left: -3px;
    transform: rotate(-45deg);
  }
  
  &::after {
    top: -2px;
    right: -3px;
    transform: rotate(45deg);
  }
`;

const StickmanLegs = styled.div`
  position: absolute;
  top: 23px;
  left: 50%;
  transform: translateX(-50%);
  
  &::before,
  &::after {
    content: '';
    position: absolute;
    width: 2px;
    height: 10px;
    background: #0ea5e9;
    box-shadow: 0 0 5px rgba(14, 165, 233, 0.3);
  }
  
  &::before {
    left: -3px;
    transform: rotate(-15deg);
  }
  
  &::after {
    right: -3px;
    transform: rotate(15deg);
  }
`;

const ToolsContainer = styled.div`
  position: absolute;
  top: 5px;
  left: 35px;
  opacity: 0;
  transition: all 0.3s ease;
  pointer-events: none;
`;

const Hammer = styled.div`
  position: absolute;
  width: 8px;
  height: 2px;
  background: #8b4513;
  transform-origin: left center;
  animation: ${hammerSwing} 1s ease-in-out infinite;
  
  &::before {
    content: '';
    position: absolute;
    right: -4px;
    top: -2px;
    width: 4px;
    height: 6px;
    background: #696969;
    border-radius: 1px;
  }
`;

const Wrench = styled.div`
  position: absolute;
  top: 10px;
  left: 5px;
  width: 10px;
  height: 2px;
  background: #696969;
  border-radius: 1px;
  transform-origin: center;
  animation: ${wrenchTurn} 1.5s ease-in-out infinite;
  
  &::before {
    content: '';
    position: absolute;
    right: -2px;
    top: -1px;
    width: 4px;
    height: 4px;
    border: 1px solid #696969;
    border-radius: 2px;
    background: transparent;
  }
`;

const SparksContainer = styled.div`
  position: absolute;
  top: 15px;
  right: 15px;
  opacity: 0;
  transition: all 0.3s ease;
  pointer-events: none;
  
  .spark {
    position: absolute;
    width: 3px;
    height: 3px;
    background: #fbbf24;
    border-radius: 50%;
    animation: ${sparkle} 0.8s ease-in-out infinite;
    
    &:nth-child(1) {
      top: 0;
      left: 0;
      animation-delay: 0s;
    }
    
    &:nth-child(2) {
      top: -5px;
      left: 5px;
      animation-delay: 0.2s;
    }
    
    &:nth-child(3) {
      top: 3px;
      left: -3px;
      animation-delay: 0.4s;
    }
    
    &:nth-child(4) {
      top: -2px;
      left: -5px;
      animation-delay: 0.6s;
    }
  }
`;

const ProgressBar = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: rgba(255, 255, 255, 0.1);
  overflow: hidden;
  
  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, #10b981, #0ea5e9);
    animation: ${props => props.isActive ? css`
      animation: progressFill 3s ease-in-out infinite;
      
      @keyframes progressFill {
        0% {
          left: -100%;
        }
        100% {
          left: 100%;
        }
      }
    ` : 'none'};
  }
`;

function AnimatedLogo() {
  const [isActive, setIsActive] = useState(false);
  const [isFixed, setIsFixed] = useState(false);
  const [isWalkingIn, setIsWalkingIn] = useState(false);

  useEffect(() => {
    // Start the walking animation on component mount
    const walkTimer = setTimeout(() => {
      setIsWalkingIn(true);
    }, 500);

    // Start fixing animation after stickman arrives
    const fixTimer = setTimeout(() => {
      setIsActive(true);
      
      // Complete the fix after some time
      setTimeout(() => {
        setIsFixed(true);
        setIsActive(false);
      }, 3000);
    }, 2500);

    return () => {
      clearTimeout(walkTimer);
      clearTimeout(fixTimer);
    };
  }, []);

  const handleClick = () => {
    // Reset and restart the animation
    setIsFixed(false);
    setIsActive(false);
    setIsWalkingIn(false);
    
    setTimeout(() => {
      setIsWalkingIn(true);
      setTimeout(() => {
        setIsActive(true);
        setTimeout(() => {
          setIsFixed(true);
          setIsActive(false);
        }, 3000);
      }, 1000);
    }, 100);
  };

  return (
    <LogoContainer onClick={handleClick}>
      <Stickman className="stickman" isWalkingIn={isWalkingIn}>
        <StickmanArms />
        <StickmanLegs />
      </Stickman>
      
      <ToolsContainer className="tools">
        <Hammer />
        <Wrench />
      </ToolsContainer>
      
      <SparksContainer className="sparks">
        <div className="spark" />
        <div className="spark" />
        <div className="spark" />
        <div className="spark" />
      </SparksContainer>
      
      <BrokenItem isFixed={isFixed} />
      
      <ProgressBar isActive={isActive} />
    </LogoContainer>
  );
}

export default AnimatedLogo;
