import React, { useState, useEffect } from 'react';
import styled, { createGlobalStyle, keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import TicketForm from './components/TicketForm';
import TicketResponse from './components/TicketResponse';
import Header from './components/Header';
import Background from './components/Background';

const float = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  33% { transform: translateY(-10px) rotate(1deg); }
  66% { transform: translateY(5px) rotate(-1deg); }
`;

const pulse = keyframes`
  0%, 100% { opacity: 0.3; }
  50% { opacity: 0.8; }
`;

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    background: radial-gradient(circle at 50% 50%, #0a0a0a 0%, #000000 100%);
    color: #ffffff;
    min-height: 100vh;
    overflow-x: hidden;
    cursor: none;
  }

  html {
    scroll-behavior: smooth;
  }

  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: #1a1a1a;
  }

  ::-webkit-scrollbar-thumb {
    background: linear-gradient(45deg, #6366f1, #8b5cf6);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(45deg, #4f46e5, #7c3aed);
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const DynamicBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 0;
  
  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(
      circle at ${props => props.mouseX || 50}% ${props => props.mouseY || 50}%,
      rgba(99, 102, 241, 0.15) 0%,
      rgba(139, 92, 246, 0.1) 25%,
      rgba(168, 85, 247, 0.05) 50%,
      transparent 70%
    );
    transition: all 0.3s ease;
    animation: ${pulse} 8s ease-in-out infinite;
  }
  
  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: 
      radial-gradient(circle at 20% 80%, rgba(16, 185, 129, 0.1) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, rgba(245, 158, 11, 0.1) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, rgba(59, 130, 246, 0.05) 0%, transparent 50%);
    animation: ${float} 20s ease-in-out infinite;
  }
`;

const FloatingOrbs = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 1;
  
  .orb {
    position: absolute;
    border-radius: 50%;
    filter: blur(1px);
    animation: ${float} 15s ease-in-out infinite;
    
    &:nth-child(1) {
      width: 100px;
      height: 100px;
      top: 10%;
      left: 10%;
      background: radial-gradient(circle, rgba(99, 102, 241, 0.1), transparent);
      animation-delay: 0s;
    }
    
    &:nth-child(2) {
      width: 150px;
      height: 150px;
      top: 60%;
      right: 10%;
      background: radial-gradient(circle, rgba(139, 92, 246, 0.08), transparent);
      animation-delay: -5s;
    }
    
    &:nth-child(3) {
      width: 80px;
      height: 80px;
      bottom: 20%;
      left: 20%;
      background: radial-gradient(circle, rgba(16, 185, 129, 0.1), transparent);
      animation-delay: -10s;
    }
    
    &:nth-child(4) {
      width: 120px;
      height: 120px;
      top: 30%;
      right: 30%;
      background: radial-gradient(circle, rgba(245, 158, 11, 0.08), transparent);
      animation-delay: -7s;
    }
  }
`;

const CustomCursor = styled.div`
  position: fixed;
  width: 20px;
  height: 20px;
  border: 2px solid rgba(99, 102, 241, 0.8);
  border-radius: 50%;
  pointer-events: none;
  z-index: 9999;
  transition: all 0.1s ease;
  transform: translate(-50%, -50%);
  
  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 4px;
    height: 4px;
    background: rgba(139, 92, 246, 1);
    border-radius: 50%;
    transform: translate(-50%, -50%);
  }
`;

const BrandHeader = styled(motion.div)`
  position: absolute;
  top: 2rem;
  left: 2rem;
  z-index: 10;
  
  h1 {
    font-size: 2.5rem;
    font-weight: 800;
    background: linear-gradient(135deg, #6366f1, #8b5cf6, #a855f7);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    text-shadow: 0 0 30px rgba(99, 102, 241, 0.3);
    letter-spacing: -0.02em;
  }
  
  p {
    color: rgba(255, 255, 255, 0.7);
    font-size: 0.875rem;
    margin-top: 0.5rem;
    font-weight: 500;
    letter-spacing: 0.05em;
  }
`;

const MainContent = styled.main`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  position: relative;
  z-index: 2;
`;

const ContentWrapper = styled(motion.div)`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

function App() {
  const [ticketResponse, setTicketResponse] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 50, y: 50 });
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      const x = (e.clientX / window.innerWidth) * 100;
      const y = (e.clientY / window.innerHeight) * 100;
      setMousePosition({ x, y });
      setCursorPosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const handleTicketSubmit = (response) => {
    setTicketResponse(response);
  };

  const handleNewTicket = () => {
    setTicketResponse(null);
  };

  const handleLoadingChange = (loading) => {
    setIsLoading(loading);
  };

  return (
    <>
      <GlobalStyle />
      <AppContainer>
        <DynamicBackground mouseX={mousePosition.x} mouseY={mousePosition.y} />
        <FloatingOrbs>
          <div className="orb"></div>
          <div className="orb"></div>
          <div className="orb"></div>
          <div className="orb"></div>
        </FloatingOrbs>
        <CustomCursor 
          style={{ 
            left: `${cursorPosition.x}px`, 
            top: `${cursorPosition.y}px` 
          }} 
        />
        <BrandHeader
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <h1>TriageAI</h1>
          <p>Intelligent IT Support Assistant</p>
        </BrandHeader>
        <Background />
        <Header />
        <MainContent>
          <ContentWrapper
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <AnimatePresence mode="wait">
              {!ticketResponse ? (
                <motion.div
                  key="ticket-form"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <TicketForm 
                    onSubmit={handleTicketSubmit}
                    onLoadingChange={handleLoadingChange}
                    isLoading={isLoading}
                  />
                </motion.div>
              ) : (
                <motion.div
                  key="ticket-response"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <TicketResponse 
                    response={ticketResponse}
                    onNewTicket={handleNewTicket}
                  />
                </motion.div>
              )}
            </AnimatePresence>
          </ContentWrapper>
        </MainContent>
      </AppContainer>
    </>
  );
}

export default App;
