import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import TicketForm from './components/TicketForm';
import TicketResponse from './components/TicketResponse';
import Header from './components/Header';
import Background from './components/Background';
import { useTheme } from './ThemeContext';

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
  display: flex;
  flex-direction: column;
`;

const MainContent = styled.main`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  position: relative;
  z-index: 2;
`;

const ContentWrapper = styled(motion.div)`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

function App() {
  const [ticketResponse, setTicketResponse] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { theme } = useTheme();

  const handleTicketSubmit = (response) => {
    setTicketResponse(response);
  };

  const handleNewTicket = () => {
    setTicketResponse(null);
  };

  const handleLoadingChange = (loading) => {
    setIsLoading(loading);
  };

  return (
    <AppContainer data-theme={theme}>
      <Background />
      <Header />
      <MainContent>
        <ContentWrapper
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <AnimatePresence mode="wait">
            {!ticketResponse ? (
              <motion.div
                key="ticket-form"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
              >
                <TicketForm 
                  onSubmit={handleTicketSubmit}
                  onLoadingChange={handleLoadingChange}
                  isLoading={isLoading}
                />
              </motion.div>
            ) : (
              <motion.div
                key="ticket-response"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
              >
                <TicketResponse 
                  response={ticketResponse}
                  onNewTicket={handleNewTicket}
                />
              </motion.div>
            )}
          </AnimatePresence>
        </ContentWrapper>
      </MainContent>
    </AppContainer>
  );
}

export default App;
