import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  FaCheckCircle, 
  FaExclamationTriangle, 
  FaClock, 
  FaHistory, 
  FaEnvelope,
  FaPlus,
  FaArrowUp,
  FaBug,
  FaTools,
  FaDatabase,
  FaBrain,
  FaRecycle,
  FaMagic,
  FaSpinner
} from 'react-icons/fa';
import { toast } from 'react-hot-toast';
import axios from 'axios';
import EscalationForm from './EscalationForm';

// Helper function to format solution text
const formatSolutionText = (text) => {
  if (!text) return '';
  
  // First, handle the case where numbered steps are in a single line or embedded in sentences
  let processedText = text;
  
  // Split numbered items that are in one continuous line (like "1. Step one 2. Step two 3. Step three")
  // This regex finds patterns like ". 2." or ". 3." and adds a newline before the number
  processedText = processedText.replace(/(\.\s+)(\d+\.)/g, '$1\n$2');
  
  // Also handle cases where there's no period before the number (like "step 2. next step")
  processedText = processedText.replace(/([a-z]\s+)(\d+\.)/g, '$1\n$2');
  
  // Handle cases where numbered steps appear after colons (like "follow these steps: 1. First step")
  processedText = processedText.replace(/(\:\s*)(\d+\.)/g, '$1\n$2');
  
  // Handle cases where numbered steps appear after phrases (like "steps 1. First step")
  processedText = processedText.replace(/(steps?\s+)(\d+\.)/gi, '$1\n$2');
  
  // Split text into lines first
  const lines = processedText.split('\n').map(line => line.trim()).filter(line => line.length > 0);
  
  const elements = [];
  
  lines.forEach((line, index) => {
    // Check if line contains a numbered step somewhere in the middle
    // Handle cases like "To install Microsoft Word, follow these steps: 1. First step"
    const embeddedNumberMatch = line.match(/^(.+?)(\d+\.\s+.+)$/);
    if (embeddedNumberMatch && embeddedNumberMatch[1].length > 10) {
      const prefixText = embeddedNumberMatch[1].trim();
      const numberedPart = embeddedNumberMatch[2].trim();
      
      // Add the prefix as a paragraph
      if (prefixText) {
        elements.push(<p key={`prefix-${index}`}>{prefixText}</p>);
      }
      
      // Process the numbered part
      const numberedMatch = numberedPart.match(/^(?:Step\s+)?(\d+)[\.\:\)]\s*(.+)/i);
      if (numberedMatch) {
        const stepNumber = numberedMatch[1];
        const stepText = numberedMatch[2];
        
        elements.push(
          <div key={`step-${index}`} className="formatted-step">
            <span className="step-number">{stepNumber}</span>
            <span className="step-text">{stepText}</span>
          </div>
        );
        return;
      }
    }
    
    // Check if it's a numbered step (supports: "1. text", "Step 1: text", "1) text")
    const numberedMatch = line.match(/^(?:Step\s+)?(\d+)[\.\:\)]\s*(.+)/i);
    if (numberedMatch) {
      const stepNumber = numberedMatch[1];
      const stepText = numberedMatch[2];
      
      elements.push(
        <div key={`step-${index}`} className="formatted-step">
          <span className="step-number">{stepNumber}</span>
          <span className="step-text">{stepText}</span>
        </div>
      );
      return;
    }
    
    // Check if it's a bullet point
    const bulletMatch = line.match(/^[-*â€¢]\s*(.+)/);
    if (bulletMatch) {
      elements.push(
        <div key={`bullet-${index}`} className="formatted-bullet">
          <span className="bullet-marker">â€¢</span>
          <span className="bullet-text">{bulletMatch[1]}</span>
        </div>
      );
      return;
    }
    
    // Check if it's a header (ends with : and is short)
    if (line.endsWith(':') && line.length < 100) {
      elements.push(<h4 key={`h4-${index}`}>{line}</h4>);
      return;
    }
    
    // Regular text - make it a paragraph if it's substantial
    if (line.length > 10) {
      elements.push(<p key={`p-${index}`}>{line}</p>);
    } else if (line.length > 0) {
      elements.push(<span key={`span-${index}`}>{line}<br /></span>);
    }
  });
  
  return elements.length > 0 ? elements : <p>{text}</p>;
};

const ResponseContainer = styled(motion.div)`
  max-width: 1000px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const TicketHeader = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(25px);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 24px;
  padding: 2rem;
  position: relative;
  overflow: hidden;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3), 
              inset 0 1px 0 rgba(255, 255, 255, 0.1);
