import React, { useState, useEffect } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import TicketForm from './components/TicketForm';
import TicketResponse from './components/TicketResponse';
import Header from './components/Header';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    background: #0a0a0a;
    color: #ffffff;
    min-height: 100vh;
    overflow-x: hidden;
    position: relative;
  }

  html {
    scroll-behavior: smooth;
  }

  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: #1a1a1a;
  }

  ::-webkit-scrollbar-thumb {
    background: linear-gradient(45deg, #6366f1, #8b5cf6);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(45deg, #4f46e5, #7c3aed);
  }
`;

const AnimatedBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  background: linear-gradient(135deg, #0a0a0a 0%, #1a0a2e 40%, #16213e 100%);
  overflow: hidden;
  cursor: none;
  
  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(
      circle at var(--mouse-x, 50%) var(--mouse-y, 50%),
      rgba(139, 92, 246, 0.5) 0%,
      rgba(236, 72, 153, 0.4) 20%,
      rgba(59, 130, 246, 0.35) 40%,
      rgba(16, 185, 129, 0.25) 60%,
      transparent 80%
    ),
    radial-gradient(
      circle at calc(var(--mouse-x, 50%) + 30%) calc(var(--mouse-y, 50%) - 20%),
      rgba(16, 185, 129, 0.4) 0%,
      rgba(99, 102, 241, 0.3) 25%,
      rgba(139, 92, 246, 0.2) 50%,
      transparent 70%
    ),
    radial-gradient(
      circle at calc(var(--mouse-x, 50%) - 20%) calc(var(--mouse-y, 50%) + 25%),
      rgba(236, 72, 153, 0.45) 0%,
      rgba(139, 92, 246, 0.3) 30%,
      rgba(59, 130, 246, 0.25) 50%,
      transparent 75%
    );
    animation: fluidRotate 20s linear infinite;
    transition: all 0.15s cubic-bezier(0.4, 0, 0.2, 1);
    will-change: transform;
    mix-blend-mode: screen;
  }
  
  &::after {
    content: '';
    position: absolute;
    top: -30%;
    left: -30%;
    width: 160%;
    height: 160%;
    background: 
      radial-gradient(
        ellipse at calc(var(--mouse-x, 50%) - 10%) calc(var(--mouse-y, 50%) + 15%),
        rgba(59, 130, 246, 0.3) 0%,
        rgba(16, 185, 129, 0.2) 30%,
        rgba(99, 102, 241, 0.15) 60%,
        transparent 80%
      ),
      radial-gradient(
        ellipse at calc(var(--mouse-x, 50%) + 25%) calc(var(--mouse-y, 50%) - 10%),
        rgba(139, 92, 246, 0.35) 0%,
        rgba(236, 72, 153, 0.25) 35%,
        rgba(16, 185, 129, 0.18) 65%,
        transparent 85%
      ),
      conic-gradient(
        from calc(var(--mouse-angle, 0deg) + 45deg) at var(--mouse-x, 50%) var(--mouse-y, 50%),
        rgba(99, 102, 241, 0.2) 0deg,
        rgba(236, 72, 153, 0.25) 60deg,
        rgba(16, 185, 129, 0.2) 120deg,
        rgba(139, 92, 246, 0.22) 180deg,
        rgba(59, 130, 246, 0.18) 240deg,
        rgba(236, 72, 153, 0.2) 300deg,
        rgba(99, 102, 241, 0.2) 360deg
      );
    animation: fluidRotateReverse 25s linear infinite reverse;
    transition: all 0.1s cubic-bezier(0.4, 0, 0.2, 1);
    will-change: transform;
    mix-blend-mode: multiply;
  }

  /* Floating particle effect */
  &:hover::before {
    background: radial-gradient(
      circle at var(--mouse-x, 50%) var(--mouse-y, 50%),
      rgba(139, 92, 246, 0.7) 0%,
      rgba(236, 72, 153, 0.6) 15%,
      rgba(59, 130, 246, 0.5) 30%,
      rgba(16, 185, 129, 0.4) 45%,
      rgba(99, 102, 241, 0.3) 60%,
      transparent 80%
    ),
    radial-gradient(
      circle at calc(var(--mouse-x, 50%) + 30%) calc(var(--mouse-y, 50%) - 20%),
      rgba(16, 185, 129, 0.6) 0%,
      rgba(99, 102, 241, 0.5) 20%,
      rgba(139, 92, 246, 0.4) 40%,
      transparent 70%
    ),
    radial-gradient(
      circle at calc(var(--mouse-x, 50%) - 20%) calc(var(--mouse-y, 50%) + 25%),
      rgba(236, 72, 153, 0.65) 0%,
      rgba(139, 92, 246, 0.5) 25%,
      rgba(59, 130, 246, 0.4) 45%,
      transparent 75%
    );
    animation-duration: 8s;
    transform: scale(1.2) rotate(calc(var(--mouse-angle, 0deg) * 0.5));
  }

  &:hover::after {
    animation-duration: 10s;
    transform: scale(1.1) rotate(calc(var(--mouse-angle, 0deg) * -0.3));
    opacity: 0.9;
  }

  @keyframes fluidRotate {
    0% {
      transform: rotate(0deg) scale(1) translate(0, 0);
      filter: hue-rotate(0deg) brightness(1) blur(0px);
    }
    25% {
      transform: rotate(90deg) scale(1.05) translate(15px, -8px);
      filter: hue-rotate(20deg) brightness(1.15) blur(1px);
    }
    50% {
      transform: rotate(180deg) scale(0.98) translate(-12px, 20px);
      filter: hue-rotate(40deg) brightness(1.08) blur(0.5px);
    }
    75% {
      transform: rotate(270deg) scale(1.02) translate(8px, -15px);
      filter: hue-rotate(60deg) brightness(1.2) blur(1.5px);
    }
    100% {
      transform: rotate(360deg) scale(1) translate(0, 0);
      filter: hue-rotate(80deg) brightness(1) blur(0px);
    }
  }

  @keyframes fluidRotateReverse {
    0% {
      transform: rotate(360deg) scale(1) translate(0, 0);
      opacity: 0.8;
      filter: saturate(1) contrast(1);
    }
    25% {
      transform: rotate(270deg) scale(1.15) translate(-20px, 12px);
      opacity: 0.7;
      filter: saturate(1.2) contrast(1.1);
    }
    50% {
      transform: rotate(180deg) scale(0.92) translate(25px, -18px);
      opacity: 0.9;
      filter: saturate(0.9) contrast(1.05);
    }
    75% {
      transform: rotate(90deg) scale(1.08) translate(-15px, 22px);
      opacity: 0.6;
      filter: saturate(1.3) contrast(1.15);
    }
    100% {
      transform: rotate(0deg) scale(1) translate(0, 0);
      opacity: 0.8;
      filter: saturate(1) contrast(1);
    }
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
  display: flex;
  flex-direction: column;
`;

const MainContent = styled.main`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  position: relative;
  z-index: 2;
`;

const ContentWrapper = styled(motion.div)`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

function App() {
  const [ticketResponse, setTicketResponse] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 50, y: 50 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      const x = (e.clientX / window.innerWidth) * 100;
      const y = (e.clientY / window.innerHeight) * 100;
      const angle = Math.atan2(e.clientY - window.innerHeight / 2, e.clientX - window.innerWidth / 2) * (180 / Math.PI);
      
      setMousePosition({ x, y, angle });
      
      // Update CSS custom properties for fluid background movement
      document.documentElement.style.setProperty('--mouse-x', `${x}%`);
      document.documentElement.style.setProperty('--mouse-y', `${y}%`);
      document.documentElement.style.setProperty('--mouse-angle', `${angle}deg`);
    };

    const debouncedMouseMove = debounce(handleMouseMove, 10);
    window.addEventListener('mousemove', debouncedMouseMove);
    
    return () => {
      window.removeEventListener('mousemove', debouncedMouseMove);
    };
  }, []);

  // Debounce function for smooth performance
  function debounce(func, wait) {
    let timeout;
    return function executedFunction(...args) {
      const later = () => {
        clearTimeout(timeout);
        func(...args);
      };
      clearTimeout(timeout);
      timeout = setTimeout(later, wait);
    };
  }

  const handleTicketSubmit = (response) => {
    setTicketResponse(response);
  };

  const handleNewTicket = () => {
    setTicketResponse(null);
  };

  const handleLoadingChange = (loading) => {
    setIsLoading(loading);
  };

  return (
    <>
      <GlobalStyle />
      <AnimatedBackground />
      <AppContainer>
        <Header />
        <MainContent>
          <ContentWrapper
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <AnimatePresence mode="wait">
              {!ticketResponse ? (
                <motion.div
                  key="ticket-form"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <TicketForm 
                    onSubmit={handleTicketSubmit}
                    onLoadingChange={handleLoadingChange}
                    isLoading={isLoading}
                  />
                </motion.div>
              ) : (
                <motion.div
                  key="ticket-response"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <TicketResponse 
                    response={ticketResponse}
                    onNewTicket={handleNewTicket}
                  />
                </motion.div>
              )}
            </AnimatePresence>
          </ContentWrapper>
        </MainContent>
      </AppContainer>
    </>
  );
}

export default App;
