import React from 'react';
import styled from 'styled-components';
import { useTheme } from '../ThemeContext';

const ToggleWrapper = styled.div`
  display: flex;
  align-items: center;
  cursor: pointer;
  user-select: none;
`;

const Switch = styled.div`
  width: 48px;
  height: 26px;
  border-radius: 13px;
  background: ${({ themeMode }) =>
    themeMode === 'dark'
      ? 'linear-gradient(90deg, #23272f 60%, #10b981 100%)'
      : 'linear-gradient(90deg, #e5e7eb 60%, #6366f1 100%)'};
  position: relative;
  transition: background 0.4s cubic-bezier(0.4,0,0.2,1);
`;

const Knob = styled.div`
  position: absolute;
  top: 3px;
  left: ${({ themeMode }) => (themeMode === 'dark' ? '3px' : 'calc(100% - 23px)')};
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: #fff;
  box-shadow: 0 2px 8px rgba(0,0,0,0.15);
  transition: left 0.35s cubic-bezier(0.4,0,0.2,1);
`;

const Sun = styled.span`
  font-size: 1.1rem;
  color: #fbbf24;
  margin-left: 10px;
`;
const Moon = styled.span`
  font-size: 1.1rem;
  color: #6366f1;
  margin-right: 10px;
`;

export default function ThemeToggle() {
  const { theme, toggleTheme } = useTheme();
  return (
    <ToggleWrapper onClick={toggleTheme} title={`Switch to ${theme === 'dark' ? 'light' : 'dark'} mode`}>
      <Moon>üåô</Moon>
      <Switch themeMode={theme}>
        <Knob themeMode={theme} />
      </Switch>
      <Sun>‚òÄÔ∏è</Sun>
    </ToggleWrapper>
  );
}
