import React, { useState, useEffect } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import TicketForm from './components/TicketForm';
import TicketResponse from './components/TicketResponse';
import Header from './components/Header';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    background: #000000;
    color: #ffffff;
    min-height: 100vh;
    overflow-x: hidden;
    position: relative;
    cursor: default;
  }

  html {
    scroll-behavior: smooth;
  }

  /* Restore cursor for interactive elements */
  button, input, textarea, select, a, [role="button"] {
    cursor: pointer !important;
  }

  input, textarea {
    cursor: text !important;
  }

  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: #1a1a1a;
  }

  ::-webkit-scrollbar-thumb {
    background: linear-gradient(45deg, #666666, #999999);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(45deg, #777777, #aaaaaa);
  }
`;

const FloatingShape = styled.div`
  position: absolute;
  pointer-events: none;
  z-index: 1;
  opacity: 0.6;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  will-change: transform;
  
  &.circle {
    width: 24px;
    height: 24px;
    background: rgba(255, 255, 255, 0.8);
    border-radius: 50%;
    box-shadow: 0 0 30px rgba(255, 255, 255, 0.3);
  }
  
  &.square {
    width: 18px;
    height: 18px;
    background: rgba(255, 255, 255, 0.7);
    border-radius: 3px;
    transform: rotate(45deg);
    box-shadow: 0 0 24px rgba(255, 255, 255, 0.2);
  }
  
  &.triangle {
    width: 0;
    height: 0;
    border-left: 12px solid transparent;
    border-right: 12px solid transparent;
    border-bottom: 21px solid rgba(255, 255, 255, 0.6);
    filter: drop-shadow(0 0 18px rgba(255, 255, 255, 0.2));
  }
  
  &.diamond {
    width: 24px;
    height: 24px;
    background: rgba(255, 255, 255, 0.5);
    transform: rotate(45deg);
    border-radius: 6px;
    box-shadow: 0 0 36px rgba(255, 255, 255, 0.3);
  }
  
  &.hexagon {
    width: 30px;
    height: 17.31px;
    background: rgba(255, 255, 255, 0.6);
    position: relative;
    
    &:before,
    &:after {
      content: "";
      position: absolute;
      width: 0;
      border-left: 15px solid transparent;
      border-right: 15px solid transparent;
    }
    
    &:before {
      bottom: 100%;
      border-bottom: 8.67px solid rgba(255, 255, 255, 0.6);
    }
    
    &:after {
      top: 100%;
      border-top: 8.67px solid rgba(255, 255, 255, 0.6);
    }
  }
  
  &.star {
    width: 0;
    height: 0;
    border-left: 12px solid transparent;
    border-right: 12px solid transparent;
    border-bottom: 9px solid rgba(255, 255, 255, 0.7);
    transform: rotate(35deg);
    position: relative;
    filter: drop-shadow(0 0 24px rgba(255, 255, 255, 0.2));
    
    &:before {
      content: '';
      position: absolute;
      left: -12px;
      top: -6px;
      width: 0;
      height: 0;
      border-left: 12px solid transparent;
      border-right: 12px solid transparent;
      border-bottom: 9px solid rgba(255, 255, 255, 0.7);
      transform: rotate(-70deg);
    }
  }

  @keyframes float {
    0%, 100% {
      transform: translateY(0px) rotate(0deg);
    }
    33% {
      transform: translateY(-40px) rotate(120deg);
    }
    66% {
      transform: translateY(30px) rotate(240deg);
    }
  }
  
  @keyframes floatSlow {
    0%, 100% {
      transform: translateX(0px) translateY(0px) rotate(0deg);
    }
    25% {
      transform: translateX(35px) translateY(-25px) rotate(90deg);
    }
    50% {
      transform: translateX(-20px) translateY(40px) rotate(180deg);
    }
    75% {
      transform: translateX(-30px) translateY(-18px) rotate(270deg);
    }
  }
  
  &.animate-1 { animation: float 2.5s ease-in-out infinite; }
  &.animate-2 { animation: floatSlow 3s ease-in-out infinite; }
  &.animate-3 { animation: float 2.8s ease-in-out infinite reverse; }
  &.animate-4 { animation: floatSlow 3.5s ease-in-out infinite reverse; }
`;

const FloatingShapes = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 1;
  overflow: hidden;
`;

const AnimatedBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  background: linear-gradient(135deg, #000000 0%, #1a1a1a 40%, #2a2a2a 100%);
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: -20%;
    left: -20%;
    width: 140%;
    height: 140%;
    background: radial-gradient(
      circle at var(--mouse-x, 50%) var(--mouse-y, 50%),
      rgba(255, 255, 255, 0.08) 0%,
      rgba(200, 200, 200, 0.06) 30%,
      rgba(150, 150, 150, 0.04) 60%,
      transparent 80%
    );
    transition: all 0.3s ease-out;
    will-change: background;
  }
  
  &::after {
    content: '';
    position: absolute;
    top: -10%;
    left: -10%;
    width: 120%;
    height: 120%;
    background: radial-gradient(
      ellipse at calc(var(--mouse-x, 50%) + 20%) calc(var(--mouse-y, 50%) - 15%),
      rgba(255, 255, 255, 0.05) 0%,
      rgba(180, 180, 180, 0.04) 40%,
      transparent 70%
    );
    animation: gentleFloat 15s ease-in-out infinite;
    transition: opacity 0.3s ease-out;
  }

  &:hover::before {
    background: radial-gradient(
      circle at var(--mouse-x, 50%) var(--mouse-y, 50%),
      rgba(255, 255, 255, 0.15) 0%,
      rgba(220, 220, 220, 0.12) 25%,
      rgba(180, 180, 180, 0.08) 50%,
      rgba(140, 140, 140, 0.05) 70%,
      transparent 85%
    );
  }

  &:hover::after {
    opacity: 0.8;
    animation-duration: 8s;
  }

  @keyframes gentleFloat {
    0%, 100% {
      transform: translate(0, 0) rotate(0deg);
      opacity: 0.6;
    }
    25% {
      transform: translate(10px, -5px) rotate(1deg);
      opacity: 0.8;
    }
    50% {
      transform: translate(-5px, 10px) rotate(-0.5deg);
      opacity: 0.7;
    }
    75% {
      transform: translate(8px, -8px) rotate(0.8deg);
      opacity: 0.9;
    }
  }
`;

const AppContainer = styled.div`
  min-height: 100vh;
  position: relative;
  display: flex;
  flex-direction: column;
`;

const MainContent = styled.main`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
  position: relative;
  z-index: 2;
`;

const ContentWrapper = styled(motion.div)`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

function App() {
  const [ticketResponse, setTicketResponse] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [shapes, setShapes] = useState([]);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  // Initialize floating shapes
  useEffect(() => {
    const shapeTypes = ['circle', 'square', 'triangle', 'diamond', 'hexagon', 'star'];
    const animationTypes = ['animate-1', 'animate-2', 'animate-3', 'animate-4'];
    
    const initialShapes = Array.from({ length: 12 }, (_, i) => ({
      id: i,
      type: shapeTypes[i % shapeTypes.length],
      animation: animationTypes[i % animationTypes.length],
      x: Math.random() * (window.innerWidth - 100),
      y: Math.random() * (window.innerHeight - 100),
      baseX: Math.random() * (window.innerWidth - 100),
      baseY: Math.random() * (window.innerHeight - 100),
      vx: (Math.random() - 0.5) * 3, // Free-flowing velocity
      vy: (Math.random() - 0.5) * 3, // Free-flowing velocity
      targetX: Math.random() * (window.innerWidth - 100),
      targetY: Math.random() * (window.innerHeight - 100),
      wanderAngle: Math.random() * Math.PI * 2, // For wandering behavior
    }));
    
    setShapes(initialShapes);
  }, []);

  useEffect(() => {
    const handleMouseMove = (e) => {
      const x = (e.clientX / window.innerWidth) * 100;
      const y = (e.clientY / window.innerHeight) * 100;
      
      setMousePos({ x: e.clientX, y: e.clientY });
      
      // Update CSS custom properties for fluid background movement (throttled)
      document.documentElement.style.setProperty('--mouse-x', `${x}%`);
      document.documentElement.style.setProperty('--mouse-y', `${y}%`);
    };

    // Use throttle for smoother real-time tracking with better performance
    const throttledMouseMove = throttle(handleMouseMove, 100); // 10fps for smooth but performant tracking
    window.addEventListener('mousemove', throttledMouseMove);
    
    return () => {
      window.removeEventListener('mousemove', throttledMouseMove);
    };
  }, []);

  // Update shapes position with free-flowing movement
  useEffect(() => {
    if (shapes.length === 0) return;
    
    const updateShapes = () => {
      setShapes(prevShapes => 
        prevShapes.map(shape => {
          const dx = mousePos.x - shape.x;
          const dy = mousePos.y - shape.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          
          let newX = shape.x;
          let newY = shape.y;
          let newVx = shape.vx;
          let newVy = shape.vy;
          let newWanderAngle = shape.wanderAngle;
          let newTargetX = shape.targetX;
          let newTargetY = shape.targetY;
          
          // Mouse repulsion with smooth force
          if (distance < 120 && distance > 0) {
            const force = (120 - distance) / 120 * 0.5;
            const angle = Math.atan2(dy, dx);
            newVx -= Math.cos(angle) * force;
            newVy -= Math.sin(angle) * force;
          }
          
          // Wandering behavior - create smooth, organic movement
          newWanderAngle += (Math.random() - 0.5) * 0.3;
          const wanderForce = 0.15;
          newVx += Math.cos(newWanderAngle) * wanderForce;
          newVy += Math.sin(newWanderAngle) * wanderForce;
          
          // Gentle attraction to random targets (changes periodically)
          if (Math.random() < 0.005) { // 0.5% chance per frame to pick new target
            newTargetX = Math.random() * (window.innerWidth - 100);
            newTargetY = Math.random() * (window.innerHeight - 100);
          }
          
          const targetDx = newTargetX - shape.x;
          const targetDy = newTargetY - shape.y;
          const targetDistance = Math.sqrt(targetDx * targetDx + targetDy * targetDy);
          
          if (targetDistance > 50) {
            const targetForce = 0.08;
            newVx += (targetDx / targetDistance) * targetForce;
            newVy += (targetDy / targetDistance) * targetForce;
          }
          
          // Apply friction for smooth deceleration
          newVx *= 0.985;
          newVy *= 0.985;
          
          // Limit maximum speed for smooth movement
          const maxSpeed = 2.5;
          const currentSpeed = Math.sqrt(newVx * newVx + newVy * newVy);
          if (currentSpeed > maxSpeed) {
            newVx = (newVx / currentSpeed) * maxSpeed;
            newVy = (newVy / currentSpeed) * maxSpeed;
          }
          
          // Update position
          newX += newVx;
          newY += newVy;
          
          // Smooth boundary bouncing
          const margin = 50;
          if (newX < margin) {
            newX = margin;
            newVx = Math.abs(newVx) * 0.8;
          } else if (newX > window.innerWidth - margin) {
            newX = window.innerWidth - margin;
            newVx = -Math.abs(newVx) * 0.8;
          }
          
          if (newY < margin) {
            newY = margin;
            newVy = Math.abs(newVy) * 0.8;
          } else if (newY > window.innerHeight - margin) {
            newY = window.innerHeight - margin;
            newVy = -Math.abs(newVy) * 0.8;
          }
          
          return {
            ...shape,
            x: newX,
            y: newY,
            vx: newVx,
            vy: newVy,
            wanderAngle: newWanderAngle,
            targetX: newTargetX,
            targetY: newTargetY,
          };
        })
      );
    };

    // Ultra-smooth 60fps updates for fluid motion
    const interval = setInterval(updateShapes, 16); // 60fps for butter-smooth movement
    return () => clearInterval(interval);
  }, [mousePos.x, mousePos.y]);

  // Throttle function for better performance
  function throttle(func, limit) {
    let inThrottle;
    return function(...args) {
      if (!inThrottle) {
        func.apply(this, args);
        inThrottle = true;
        setTimeout(() => inThrottle = false, limit);
      }
    };
  }

  const handleTicketSubmit = (response) => {
    setTicketResponse(response);
  };

  const handleNewTicket = () => {
    setTicketResponse(null);
  };

  const handleLoadingChange = (loading) => {
    setIsLoading(loading);
  };

  return (
    <>
      <GlobalStyle />
      <AnimatedBackground />
      <FloatingShapes>
        {shapes.map(shape => (
          <FloatingShape
            key={shape.id}
            className={`${shape.type} ${shape.animation}`}
            style={{
              left: `${shape.x}px`,
              top: `${shape.y}px`,
            }}
          />
        ))}
      </FloatingShapes>
      <AppContainer>
        <Header />
        <MainContent>
          <ContentWrapper
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <AnimatePresence mode="wait">
              {!ticketResponse ? (
                <motion.div
                  key="ticket-form"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <TicketForm 
                    onSubmit={handleTicketSubmit}
                    onLoadingChange={handleLoadingChange}
                    isLoading={isLoading}
                  />
                </motion.div>
              ) : (
                <motion.div
                  key="ticket-response"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  <TicketResponse 
                    response={ticketResponse}
                    onNewTicket={handleNewTicket}
                  />
                </motion.div>
              )}
            </AnimatePresence>
          </ContentWrapper>
        </MainContent>
      </AppContainer>
    </>
  );
}

export default App;
