import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useTheme } from '../ThemeContext';

const BackgroundContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  overflow: hidden;
`;

const GradientBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: ${({ themeMode }) => 
    themeMode === 'light' 
      ? `linear-gradient(
          135deg,
          #f8fafc 0%,
          #e2e8f0 25%,
          #cbd5e1 50%,
          #e2e8f0 75%,
          #f8fafc 100%
        )`
      : `linear-gradient(
          135deg,
          #0c0c0c 0%,
          #1a1a1a 25%,
          #262626 50%,
          #1a1a1a 75%,
          #0c0c0c 100%
        )`};
  transition: all 0.4s cubic-bezier(0.4,0,0.2,1);
`;

const FloatingOrb = styled(motion.div)`
  position: absolute;
  border-radius: 50%;
  filter: blur(40px);
  opacity: ${({ themeMode }) => themeMode === 'light' ? '0.3' : '0.6'};
  transition: all 0.4s cubic-bezier(0.4,0,0.2,1);
`;

const Orb1 = styled(FloatingOrb)`
  width: 300px;
  height: 300px;
  background: ${({ themeMode }) => 
    themeMode === 'light' 
      ? 'linear-gradient(135deg, #3b82f6, #1d4ed8)' 
      : 'linear-gradient(135deg, #6366f1, #8b5cf6)'};
  top: 10%;
  left: -10%;
`;

const Orb2 = styled(FloatingOrb)`
  width: 200px;
  height: 200px;
  background: ${({ themeMode }) => 
    themeMode === 'light' 
      ? 'linear-gradient(135deg, #06b6d4, #0ea5e9)' 
      : 'linear-gradient(135deg, #ec4899, #f59e0b)'};
  top: 60%;
  right: -5%;
`;

const Orb3 = styled(FloatingOrb)`
  width: 150px;
  height: 150px;
  background: ${({ themeMode }) => 
    themeMode === 'light' 
      ? 'linear-gradient(135deg, #10b981, #059669)' 
      : 'linear-gradient(135deg, #10b981, #3b82f6)'};
  bottom: 20%;
  left: 20%;
`;

const GridPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: ${({ themeMode }) => 
    themeMode === 'light' 
      ? `linear-gradient(rgba(71, 85, 105, 0.05) 1px, transparent 1px),
         linear-gradient(90deg, rgba(71, 85, 105, 0.05) 1px, transparent 1px)`
      : `linear-gradient(rgba(255, 255, 255, 0.03) 1px, transparent 1px),
         linear-gradient(90deg, rgba(255, 255, 255, 0.03) 1px, transparent 1px)`};
  background-size: 50px 50px;
  mask-image: radial-gradient(circle at 50% 50%, black 40%, transparent 80%);
  transition: all 0.4s cubic-bezier(0.4,0,0.2,1);
`;

const NoiseOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='1' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='0.02'/%3E%3C/svg%3E");
  opacity: ${({ themeMode }) => themeMode === 'light' ? '0.5' : '1'};
  transition: all 0.4s cubic-bezier(0.4,0,0.2,1);
`;

function Background() {
  const { theme } = useTheme();
  
  return (
    <BackgroundContainer>
      <GradientBackground themeMode={theme} />
      <GridPattern themeMode={theme} />
      <NoiseOverlay themeMode={theme} />
      
      <Orb1
        themeMode={theme}
        animate={{
          x: [0, 50, 0],
          y: [0, 30, 0],
        }}
        transition={{
          duration: 20,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      <Orb2
        themeMode={theme}
        animate={{
          x: [0, -30, 0],
          y: [0, 50, 0],
        }}
        transition={{
          duration: 15,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
      
      <Orb3
        themeMode={theme}
        animate={{
          x: [0, 40, 0],
          y: [0, -20, 0],
        }}
        transition={{
          duration: 25,
          repeat: Infinity,
          ease: "easeInOut"
        }}
      />
    </BackgroundContainer>
  );
}

export default Background;
